import React from "react";
import { Box, Typography, Tabs, Tab, Grid, Paper, Divider } from "@mui/material";

// For demonstration, using remote icon images.
// Replace these URLs with your actual icon assets.
const getIcon = (src) => (
    <img src={src} alt="" style={{ width: 48, height: 48, display: "block", margin: "0 auto 8px" }} />
);


function Sales() {
  const salesActions = [
    { label: "Create Quick Sales", icon: getIcon("https://img.icons8.com/color/48/sell.png") },
    { label: "Create Detailed Invoice", icon: getIcon("https://img.icons8.com/color/48/invoice.png") },
    { label: "Invoices List", icon: getIcon("https://img.icons8.com/color/48/list.png") },
    { label: "Create Sales Order", icon: getIcon("https://img.icons8.com/color/48/order-history.png") },
    { label: "Sales Order List", icon: getIcon("https://img.icons8.com/color/48/bill.png") },
    { label: "Customers invoices", icon: getIcon("https://img.icons8.com/color/48/customer-insights.png") },
    { label: "Create Prospects", icon: getIcon("https://img.icons8.com/color/48/add-user-group-man-man.png") },
    { label: "Prospects List", icon: getIcon("https://img.icons8.com/color/48/conference.png") },
    { label: "New Contract", icon: getIcon("https://img.icons8.com/color/48/contract.png") },
    { label: "List of contracts", icon: getIcon("https://img.icons8.com/color/48/agreement.png") },
    { label: "Create Contact", icon: getIcon("https://img.icons8.com/color/48/add-contact-to-company.png") },
    { label: "List of contacts /address", icon: getIcon("https://img.icons8.com/color/48/address-book.png") },
    { label: "Create Customer", icon: getIcon("https://img.icons8.com/color/48/add-male-user.png") },
    { label: "Customer Details", icon: getIcon("https://img.icons8.com/color/48/user-male-info.png") },
    { label: "Template invoice", icon: getIcon("https://img.icons8.com/color/48/template.png") },
    { label: "Payments received from customers", icon: getIcon("https://img.icons8.com/color/48/money-transfer.png") },
    { label: "Create Proposal", icon: getIcon("https://img.icons8.com/color/48/add-file.png") },
    { label: "Proposals", icon: getIcon("https://img.icons8.com/color/48/documents.png") },
  ];

const settingsActions = [
    { label: "Product Accounting", icon: getIcon("https://img.icons8.com/color/48/accounting.png") },
    { label: "Vat accounts", icon: getIcon("https://img.icons8.com/color/48/tax.png") },
    { label: "Customers tags /categories", icon: getIcon("https://img.icons8.com/color/48/user-group-man-man.png") },
    { label: "Contacts tags /categories", icon: getIcon("https://img.icons8.com/color/48/new-contact.png") },
    { label: "Generate Barcode", icon: getIcon("https://img.icons8.com/color/48/barcode.png") },
  ];


  // Pie chart area is just an icon here; you should replace it with a chart library for real data
  const pieColors = {
    Draft: "#64b5f6",
    Starter: "#90caf9",
    Paid: "#1976d2",
    Closed: "#7b1fa2",
  };

  return (
    <Box sx={{ background: "#fff", borderRadius: 2, p: 4, minHeight: "80vh", boxShadow: 1 }}>
      {/* Header */}
      <Typography variant="h6" sx={{ fontWeight: 700, color: "#2272e0", mb: 2 }}>
        Customer Billing Area
      </Typography>
      
      <Tabs
        value={0}
        sx={{
          mb: 3,
          ".MuiTabs-flexContainer": { gap: 2 },
          ".MuiTab-root": {
            textTransform: "none",
            borderRadius: "24px",
            minWidth: 120,
            background: "#F4F7FC",
            fontWeight: 600,
            color: "#2272e0",
            "&.Mui-selected": {
              background: "#2272e0",
              color: "#fff",
            },
          },
        }}
        TabIndicatorProps={{ style: { display: "none" } }}
      >
        <Tab label="Sales" />
        <Tab label="Reports" />
        <Tab label="Dashboard" />
      </Tabs>
      
      <Grid container spacing={2}>
        {/* Left section: Sales and Settings */}
        <Grid item xs={8}>
          {/* Sales grid */}
          <Typography variant="subtitle1" sx={{ fontWeight: 700, mb: 1 }}>
            Sales
          </Typography>
          <Grid container spacing={2}>
            {salesActions.map((item, idx) => (
              <Grid key={idx} item xs={3} sx={{ mb: 2 }}>
                <Paper elevation={0} sx={{
                  textAlign: "center",
                  p: 2,
                  borderRadius: "14px",
                  background: "#F4F7FC",
                }}>
                  {item.icon}
                  <Typography variant="body2" sx={{ fontWeight: 500, fontSize: 14 }}>
                    {item.label}
                  </Typography>
                </Paper>
              </Grid>
            ))}
          </Grid>
          {/* Settings grid */}
          <Typography variant="subtitle1" sx={{ fontWeight: 700, mt: 3, mb: 1 }}>
            Settings
          </Typography>
          <Grid container spacing={2}>
            {settingsActions.map((item, idx) => (
              <Grid key={idx} item xs={2.4} sx={{ mb: 2 }}>
                <Paper elevation={0} sx={{
                  textAlign: "center",
                  p: 2,
                  borderRadius: "14px",
                  background: "#F4F7FC",
                }}>
                  {item.icon}
                  <Typography variant="body2" sx={{ fontWeight: 500, fontSize: 14 }}>
                    {item.label}
                  </Typography>
                </Paper>
              </Grid>
            ))}
          </Grid>
        </Grid>

        {/* Right section: Statistics */}
        <Grid item xs={4}>
          <Typography variant="subtitle1" sx={{ fontWeight: 700, mb: 2 }}>
            Statistics - Customer Invoice
          </Typography>
          <Box sx={{ position: "relative", mb: 4 }}>
            {/* For demo, placeholder donut chart. 
                For production, use react-chartjs-2, recharts, etc. */}
            <svg width="170" height="170" style={{ display: "block", margin: "0 auto" }}>
              {/* Very simple static donut chart */}
              <circle r="70" cx="85" cy="85" fill={pieColors.Draft} stroke="#fff" strokeWidth="25" />
              <path d="M85,15 A70,70 0 0,1 155,85" stroke={pieColors.Starter} strokeWidth="25" fill="none" />
              <path d="M155,85 A70,70 0 0,1 85,155" stroke={pieColors.Paid} strokeWidth="25" fill="none" />
              <path d="M85,155 A70,70 0 0,1 85,15" stroke={pieColors.Closed} strokeWidth="25" fill="none" />
            </svg>
            <Box sx={{ mt: 2 }}>
              <Typography variant="body2" sx={{ color: pieColors.Draft }}>Draft</Typography>
              <Typography variant="body2" sx={{ color: pieColors.Starter }}>Starter</Typography>
              <Typography variant="body2" sx={{ color: pieColors.Paid }}>Paid</Typography>
              <Typography variant="body2" sx={{ color: pieColors.Closed }}>Closed</Typography>
            </Box>
          </Box>
          <Divider sx={{ my: 2 }} />
          <Box sx={{ textAlign: "right" }}>
            <Typography variant="body2" sx={{ fontWeight: 500 }}>Total <span style={{ color: "#1976d2" }}>2</span></Typography>
            <Box sx={{
              height: "6px",
              background: "#e3f2fd",
              borderRadius: "4px",
              mt: 1,
              position: "relative",
              overflow: "hidden"
            }}>
              <Box sx={{
                width: "24%",
                height: "100%",
                background: "#1976d2",
                borderRadius: "4px",
              }} />
            </Box>
          </Box>
        </Grid>
      </Grid>

      {/* Footer or watermark area */}
      <Box sx={{ textAlign: "right", mt: 2 }}>
        <Typography variant="caption" sx={{ color: "#1976d2" }}>
          ECuenta
        </Typography>
      </Box>
    </Box>
  );
}

export default Sales;
