import React from "react";
import {
  Box,
  Typography,
  Tabs,
  Tab,
  Grid,
  Card,
  CardContent,
  Tooltip,
} from "@mui/material";
import {
  AddCircleOutline,
  ReceiptLong,
  ShoppingCartCheckout,
  Assignment,
  People,
  LocalOffer,
  AccountBalanceWallet,
  Payment,
  Barcode,
  FolderList,
} from "@mui/icons-material";

function a11yProps(index) {
  return {
    id: `sales-tab-${index}`,
    "aria-controls": `sales-tabpanel-${index}`,
  };
}

const salesActions = [
  { label: "Create Quick Sales", icon: <ReceiptLong fontSize="large" /> },
  { label: "Create Detailed Invoice", icon: <AddCircleOutline fontSize="large" /> },
  { label: "Invoices List", icon: <FolderList fontSize="large" /> },
  { label: "Create Sales Order", icon: <ShoppingCartCheckout fontSize="large" /> },
  { label: "Sales Order List", icon: <Assignment fontSize="large" /> },
  { label: "Customers invoices", icon: <People fontSize="large" /> },
  { label: "Create Prospects", icon: <AddCircleOutline fontSize="large" /> },
  { label: "Prospects List", icon: <People fontSize="large" /> },
  { label: "New Contract", icon: <Assignment fontSize="large" /> },
  { label: "List of contracts", icon: <FolderList fontSize="large" /> },
  { label: "Create Contact", icon: <AddCircleOutline fontSize="large" /> },
  { label: "List of contacts /address", icon: <People fontSize="large" /> },
  { label: "Create Customer", icon: <People fontSize="large" /> },
  { label: "Customer Details", icon: <People fontSize="large" /> },
  { label: "Template invoice", icon: <ReceiptLong fontSize="large" /> },
  { label: "Payments received from customers", icon: <Payment fontSize="large" /> },
  { label: "Create Proposal", icon: <AddCircleOutline fontSize="large" /> },
  { label: "Proposals", icon: <LocalOffer fontSize="large" /> },
];

const settingsActions = [
  { label: "Product Accounting", icon: <Assignment fontSize="large" /> },
  { label: "Vat accounts", icon: <AccountBalanceWallet fontSize="large" /> },
  { label: "Customers tags /categories", icon: <LocalOffer fontSize="large" /> },
  { label: "Contacts tags /categories", icon: <LocalOffer fontSize="large" /> },
  { label: "Generate Barcode", icon: <Barcode fontSize="large" /> },
];

export default function SalesHomeSection() {
  const [tab, setTab] = React.useState(0);

  const handleChange = (_, newValue) => {
    setTab(newValue);
  };

  return (
    <Box sx={{ padding: 3 }}>
      <Typography variant="h5" gutterBottom>
        Customer Billing Area
      </Typography>

      <Tabs value={tab} onChange={handleChange} aria-label="sales tabs" sx={{ mb: 3 }}>
        <Tab label="Sales" {...a11yProps(0)} />
        <Tab label="Reports" {...a11yProps(1)} />
        <Tab label="Dashboard" {...a11yProps(2)} />
      </Tabs>

      {tab === 0 && (
        <>
          <Typography variant="h6" gutterBottom>
            Sales
          </Typography>
          <Grid container spacing={2}>
            {salesActions.map(({ label, icon }) => (
              <Grid key={label} item xs={6} sm={4} md={3} lg={2}>
                <Tooltip title={label} arrow>
                  <Card
                    sx={{
                      display: "flex",
                      flexDirection: "column",
                      alignItems: "center",
                      p: 2,
                      cursor: "pointer",
                      ":hover": { backgroundColor: "#e3effe" },
                    }}
                    variant="outlined"
                  >
                    {icon}
                    <Typography variant="caption" textAlign="center" mt={1}>
                      {label}
                    </Typography>
                  </Card>
                </Tooltip>
              </Grid>
            ))}
          </Grid>

          <Typography variant="h6" gutterBottom mt={5}>
            Settings
          </Typography>
          <Grid container spacing={2}>
            {settingsActions.map(({ label, icon }) => (
              <Grid key={label} item xs={6} sm={4} md={3} lg={2}>
                <Tooltip title={label} arrow>
                  <Card
                    sx={{
                      display: "flex",
                      flexDirection: "column",
                      alignItems: "center",
                      p: 2,
                      cursor: "pointer",
                      ":hover": { backgroundColor: "#e3effe" },
                    }}
                    variant="outlined"
                  >
                    {icon}
                    <Typography variant="caption" textAlign="center" mt={1}>
                      {label}
                    </Typography>
                  </Card>
                </Tooltip>
              </Grid>
            ))}
          </Grid>
        </>
      )}

      {/* You can add similar sections for Reports and Dashboard tabs here */}

      {/* Placeholder for the chart/statistics */}
      <Box sx={{ mt: 6, maxWidth: 400 }}>
        {/* Replace this with a chart component like Recharts or Chart.js */}
        <Typography variant="subtitle1" mb={2}>
          Statistics - Customer Invoice
        </Typography>
        <Box
          sx={{
            width: 300,
            height: 300,
            borderRadius: "50%",
            background:
              "conic-gradient(#337ab7 0% 20%, #5bc0de 20% 40%, #f0ad4e 40% 60%, #d9534f 60% 100%)",
          }}
        />
        <Typography variant="caption" display="block" mt={1}>
          Draft - #337ab7, Started - #5bc0de, Paid - #f0ad4e, Closed - #d9534f
        </Typography>
      </Box>
    </Box>
  );
}
