// components/Header.js
import React from "react";
import { useLocation } from "react-router-dom";
import { Box, Typography } from "@mui/material";

function Header() {
  const location = useLocation();

  // Example: change header based on the path
  if (location.pathname === "/sales") {
    // Sales page header as per your image
    return (
      <Box
        sx={{
          display: "flex",
          alignItems: "center",
          justifyContent: "space-between",
          height: "64px",
          px: 3,
          bgcolor: "#fff",
          borderBottom: "1px solid #e0e0e0",
          width: "100vw",
          position: "fixed",
          top: 0,
          left: 0,
          zIndex: 1300,
        }}
      >
        {/* Left side: logo, hamburger, sales navigation */}
        <Box sx={{ display: "flex", alignItems: "center", gap: 2 }}>
          <img src="/logo.png" alt="Logo" style={{ width: 40, height: 40 }} />
          {/* Hamburger icon */}
          <Box sx={{ width: 32, height: 32, display: "flex", alignItems: "center", justifyContent: "center" }}>
            <span style={{ fontSize: 24 }}>‚ò∞</span>
          </Box>
          {/* Menu items */}
          {["Customers", "Sales Orders", "Contracts", "Quotations", "Customer Contact", "Invoices", "Settings"].map(item => (
            <Typography
              key={item}
              variant="body1"
              sx={{
                px: 2,
                color: "#23457d",
                fontWeight: 500,
                fontSize: 16,
                borderRight: "1px solid #e0e0e0",
                "&:last-child": { borderRight: "none" },
              }}
            >
              {item}
            </Typography>
          ))}
        </Box>
        {/* Right side: icons (use proper icon buttons in your implementation) */}
        <Box sx={{ display: "flex", alignItems: "center", gap: 2 }}>
          {/* Replace below spans with actual icons, you can use @mui/icons-material */}
          <span>üßÆ</span>
          <span>üí°</span>
          <span>‚öôÔ∏è</span>
          <span>üìÖ</span>
          <span>
            üîî
            <span style={{
              color: "#fff",
              background: "#e57373",
              borderRadius: "10px",
              fontSize: 12,
              marginLeft: 2,
              padding: "0 6px",
              position: "relative",
              top: -8
            }}>3</span>
          </span>
          <span>üî≥</span>
          <span>üåê</span>
        </Box>
      </Box>
    );
  }

  // Default header for other pages
  return (
    <Box
      sx={{
        display: "flex",
        alignItems: "center",
        height: "64px",
        px: 3,
        bgcolor: "#fff",
        borderBottom: "1px solid #e0e0e0",
        width: "100vw",
        position: "fixed",
        top: 0,
        left: 0,
        zIndex: 1300,
      }}
    >
      <img src="/logo.png" alt="Logo" style={{ width: 40, height: 40 }} />
      <Typography sx={{ ml: 2 }} variant="h5">
        Your App Title
      </Typography>
      {/* Add the rest of your default header items here */}
    </Box>
  );
}

export default Header;
