import React, { useState } from "react";
import { Box, Button, Menu, MenuItem } from "@mui/material";

const dropdowns = [
  {
    label: "Customers",
    items: ["Add Customer", "View Customers", "Customer Groups"]
  },
  {
    label: "Sales Orders",
    items: ["New Sales Order", "Sales Order List"]
  },
  {
    label: "Contracts",
    items: ["New Contract", "Contract List"]
  },
  {
    label: "Quotations",
    items: ["Create Quotation", "Quotations List"]
  },
  {
    label: "Customer Contact",
    items: ["Add Contact", "Contact List", "Address Book"]
  },
  {
    label: "Invoices",
    items: ["New Invoice", "Invoices List"]
  },
  {
    label: "Settings",
    items: ["Preferences", "User Management"]
  }
];

function HeaderDropdowns() {
  // Track which dropdown is open
  const [anchorEl, setAnchorEl] = useState(null);
  const [openIdx, setOpenIdx] = useState(null);

  const handleClick = (event, idx) => {
    setAnchorEl(event.currentTarget);
    setOpenIdx(idx);
  };

  const handleClose = () => {
    setAnchorEl(null);
    setOpenIdx(null);
  };

  return (
    <Box sx={{ display: "flex", alignItems: "center", gap: 0.5 }}>
      {dropdowns.map((dropdown, idx) => (
        <React.Fragment key={dropdown.label}>
          <Button
            variant="outlined"
            sx={{
              borderRadius: 0,
              fontWeight: 500,
              color: "#23457d",
              borderColor: "#e0e0e0",
              minWidth: 128,
              fontSize: 15,
              textTransform: "none",
              bgcolor: "#fff",
              borderRight: idx < dropdowns.length - 1 ? "1px solid #e0e0e0" : "none"
            }}
            onClick={(e) => handleClick(e, idx)}
            endIcon={<span style={{ fontSize: 18 }}>â–¼</span>}
          >
            {dropdown.label}
          </Button>
          <Menu
            anchorEl={anchorEl}
            open={openIdx === idx}
            onClose={handleClose}
            MenuListProps={{
              'aria-labelledby': `${dropdown.label}-button`,
            }}
          >
            {dropdown.items.map(item => (
              <MenuItem key={item} onClick={handleClose}>{item}</MenuItem>
            ))}
          </Menu>
        </React.Fragment>
      ))}
    </Box>
  );
}

export default HeaderDropdowns;
