import React from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Header from "./components/Header";  // Your header import
import Sidebar from "./components/Sidebar";
import { Box, Toolbar } from "@mui/material";

const Home = () => <h2>Dashboard Home</h2>;
const Sales = () => <h2>Sales Page</h2>;
const Banking = () => <h2>Banking Page</h2>;
const Users = () => <h2>Users Page</h2>;

function App() {
  return (
    <Router>
      <Box sx={{ display: "flex", flexDirection: "column", height: "100vh" }}>
        <Header />
        <Box sx={{ display: "flex", flexGrow: 1 }}>
          <Sidebar />
          <Box component="main" sx={{ flexGrow: 1, p: 3, overflowY: "auto" }}>
            <Toolbar /> {/* To offset content for fixed app bar */}
            <Routes>
              <Route path="/" element={<Home />} />
              <Route path="/sales" element={<Sales />} />
              <Route path="/banking" element={<Banking />} />
              <Route path="/users" element={<Users />} />
            </Routes>
          </Box>
        </Box>
      </Box>
    </Router>
  );
}

export default App;
