import React from "react";
import { Box, Typography, Paper, Button } from "@mui/material";

function QuickSale() {
  return (
    <Box sx={{ p: 3 }}>
      <Typography variant="h6" sx={{ fontWeight: 700, mb: 3 }}>
        New Sales Invoice
      </Typography>
      <Paper sx={{ p: 3 }}>
        {/* Your detailed sales invoice form goes here */}
        <Typography>New Sales Invoice Form content placeholder</Typography>
        <Box sx={{ mt: 2 }}>
          <Button variant="contained" color="primary">
            Save and Print
          </Button>
        </Box>
      </Paper>
    </Box>
  );
}

export default QuickSale;
