import React from "react";
import { Box, Typography, Tabs, Tab, Grid, Paper } from "@mui/material";
import PieChartOutlineIcon from '@mui/icons-material/PieChartOutline';

// Placeholder icons - replace these with your actual icon components or SVGs
const QuickSalesIcon = () => (
  <img src="https://img.icons8.com/fluency/48/000000/sales-performance.png" alt="Quick Sales" style={{ marginBottom: 8 }} />
);
const InvoiceIcon = () => (
  <img src="https://img.icons8.com/fluency/48/000000/invoice.png" alt="Invoice" style={{ marginBottom: 8 }} />
);
const SalesOrderIcon = () => (
  <img src="https://img.icons8.com/fluency/48/000000/purchase-order.png" alt="Sales Order" style={{ marginBottom: 8 }} />
);
const CustomerIcon = () => (
  <img src="https://img.icons8.com/fluency/48/000000/customer.png" alt="Customer" style={{ marginBottom: 8 }} />
);
const ContractIcon = () => (
  <img src="https://img.icons8.com/fluency/48/000000/contract.png" alt="Contract" style={{ marginBottom: 8 }} />
);
const ProposalIcon = () => (
  <img src="https://img.icons8.com/fluency/48/000000/proposal.png" alt="Proposal" style={{ marginBottom: 8 }} />
);
const BarcodeIcon = () => (
  <img src="https://img.icons8.com/fluency/48/000000/barcode.png" alt="Barcode" style={{ marginBottom: 8 }} />
);

function Sales() {
  const salesActions = [
    { label: "Create Quick Sales", icon: <QuickSalesIcon /> },
    { label: "Create Detailed Invoice", icon: <InvoiceIcon /> },
    { label: "Invoices List", icon: <InvoiceIcon /> },
    { label: "Create Sales Order", icon: <SalesOrderIcon /> },
    { label: "Sales Order List", icon: <SalesOrderIcon /> },
    { label: "Customers invoices", icon: <CustomerIcon /> },
    { label: "Create Prospects", icon: <CustomerIcon /> },
    { label: "Prospects List", icon: <CustomerIcon /> },
    { label: "New Contract", icon: <ContractIcon /> },
    { label: "List of contracts", icon: <ContractIcon /> },
    { label: "Create Contact", icon: <CustomerIcon /> },
    { label: "List of contacts /address", icon: <CustomerIcon /> },
    { label: "Create Customer", icon: <CustomerIcon /> },
    { label: "Customer Details", icon: <CustomerIcon /> },
    { label: "Template invoice", icon: <InvoiceIcon /> },
    { label: "Payments received from customers", icon: <InvoiceIcon /> },
    { label: "Create Proposal", icon: <ProposalIcon /> },
    { label: "Proposals", icon: <ProposalIcon /> },
  ];

  const settingsActions = [
    { label: "Product Accounting", icon: <InvoiceIcon /> },
    { label: "Vat accounts", icon: <InvoiceIcon /> },
    { label: "Customers tags /categories", icon: <CustomerIcon /> },
    { label: "Contacts tags /categories", icon: <CustomerIcon /> },
    { label: "Generate Barcode", icon: <BarcodeIcon /> },
  ];

  return (
    <Box sx={{ background: "#fff", borderRadius: 2, boxShadow: 1, p: 4, minHeight: "80vh" }}>
      <Typography variant="h6" sx={{ fontWeight: 700, color: "#1976d2", mb: 2 }}>
        Customer Billing Area
      </Typography>

      <Tabs
        value={0}
        sx={{
          mb: 4,
          ".MuiTabs-flexContainer": { gap: 1 },
          ".MuiTab-root": {
            textTransform: "none",
            borderRadius: "30px",
            minWidth: 120,
            background: "#F4F7FC",
            fontWeight: 600,
            color: "#1976d2",
            "&.Mui-selected": {
              background: "#1976d2",
              color: "#fff",
            },
          },
        }}
        TabIndicatorProps={{
          style: { display: "none" },
        }}
      >
        <Tab label="Sales" />
        <Tab label="Reports" />
        <Tab label="Dashboard" />
      </Tabs>

      <Grid container spacing={4}>
        {/* Sales actions section */}
        <Grid item xs={8}>
          <Typography variant="subtitle1" sx={{ fontWeight: 700, mb: 2 }}>
            Sales
          </Typography>
          <Grid container spacing={2}>
            {salesActions.map((item, idx) => (
              <Grid item xs={3} key={idx}>
                <Paper
                  sx={{
                    textAlign: "center",
                    p: 2.5,
                    borderRadius: "16px",
                    boxShadow: 0,
                    background: "#F4F7FC",
                    cursor: "pointer",
                    transition: "background-color 0.3s",
                    "&:hover": { backgroundColor: "#e3f2fd" },
                  }}
                >
                  {item.icon}
                  <Typography variant="body2" sx={{ mt: 1, fontWeight: 500 }}>
                    {item.label}
                  </Typography>
                </Paper>
              </Grid>
            ))}
          </Grid>

          {/* Settings Section */}
          <Typography variant="subtitle1" sx={{ fontWeight: 700, mt: 5, mb: 2 }}>
            Settings
          </Typography>
          <Grid container spacing={2}>
            {settingsActions.map((item, idx) => (
              <Grid item xs={2.4} key={idx}>
                <Paper
                  sx={{
                    textAlign: "center",
                    p: 2.5,
                    borderRadius: "16px",
                    boxShadow: 0,
                    background: "#F4F7FC",
                    cursor: "pointer",
                    transition: "background-color 0.3s",
                    "&:hover": { backgroundColor: "#e3f2fd" },
                  }}
                >
                  {item.icon}
                  <Typography variant="body2" sx={{ mt: 1, fontWeight: 500 }}>
                    {item.label}
                  </Typography>
                </Paper>
              </Grid>
            ))}
          </Grid>
        </Grid>

        {/* Statistics Section */}
        <Grid item xs={4}>
          <Typography variant="subtitle1" sx={{ fontWeight: 700, mb: 2 }}>
            Statistics - Customer Invoice
          </Typography>
          <Paper sx={{ p: 3, textAlign: "center", borderRadius: "16px", boxShadow: 0, background: "#F4F7FC" }}>
            {/* Placeholder Pie Chart icon - replace with actual chart component */}
            <PieChartOutlineIcon sx={{ fontSize: 120, color: "#90caf9", mb: 3 }} />
            <Box sx={{ display: "flex", flexDirection: "column", justifyContent: "center", alignItems: "start", ml: 6, mt: -5 }}>
              <Typography variant="body2" sx={{ color: "#64b5f6", fontWeight: 600 }}>Draft</Typography>
              <Typography variant="body2" sx={{ color: "#1976d2", fontWeight: 600 }}>Starter</Typography>
              <Typography variant="body2" sx={{ color: "#388e3c", fontWeight: 600 }}>Paid</Typography>
              <Typography variant="body2" sx={{ color: "#7b1fa2", fontWeight: 600 }}>Closed</Typography>
            </Box>
          </Paper>
          <Box sx={{ mt: 3, textAlign: "right" }}>
            <Typography variant="subtitle2" color="text.secondary">
              Total 2
            </Typography>
          </Box>
        </Grid>
      </Grid>
    </Box>
  );
}

export default Sales;
